using UnityEngine;using UnityEditor;public class EzFSMEditor : Editor {	static GUISkin defaultLabelSkin = Resources.Load("FSMLabelSkin") as GUISkin;	[DrawGizmo(GizmoType.InSelectionHierarchy | GizmoType.NotInSelectionHierarchy)]	static void DrawState(EzFSM fsm, GizmoType gizmoType) {				if (! fsm.ShowLabel)				return;		if (fsm.LabelGUISkin == null)						Handles.Label (	fsm.transform.position + fsm.LabelOffset,							fsm.CurrentStateName,							defaultLabelSkin.label);		else			Handles.Label (	fsm.transform.position + fsm.LabelOffset,		              		fsm.CurrentStateName,					  		fsm.LabelGUISkin.label );	}}